// Import Danfo.js and create a small dataset
exports.dfd = require('danfojs');

exports.data = {
  A: [1, 2, 4],
  B: [10, 20, 40]
};
console.log('Danfo.js imported. Data:', exports.data);

// Output module info
output({
  module: 'danfojs',
  version: exports.dfd.version || 'unknown',
  dataShape: { rows: exports.data.A.length, columns: Object.keys(exports.data).length }
});

// Create DataFrame from data
exports.df = new dfd.DataFrame(data);
console.log('DataFrame created:');
exports.df.print();

// Output DataFrame info
output({
  shape: exports.df.shape,
  columns: exports.df.columns,
  dtypes: exports.df.dtypes
});
output(exports.df);

// Add a new column C = A + B (immutable approach)
exports.df2 = df.addColumn('C', df['A'].add(df['B']), { inplace: false });
console.log('New DataFrame with column C:', exports.df2);
exports.df2.print();

// Output operation result
output({
  operation: 'addColumn',
  newColumn: 'C',
  originalShape: df.shape,
  newShape: exports.df2.shape,
  columnC_values: exports.df2['C'].values,
  inplace: false
});

// Sum column C from the new DataFrame
exports.sumC = df2['C'].sum();
console.log('Sum of column C:', exports.sumC);

// Output calculation summary
output({
  calculation: 'sum',
  column: 'C',
  result: exports.sumC,
  verification: data.A.reduce((a, b) => a + b, 0) + data.B.reduce((a, b) => a + b, 0)
});

// Demonstrate both DOM output approaches

// Scenario 1: Create DOM element and use output()
const div1 = document.createElement('div');
div1.innerHTML = '<h3>Method 1bb: output(domElement)</h3><p>DataFrame shape: ' + JSON.stringify(df2.shape) + '</p>';
div1.style.padding = '10px';
div1.style.border = '1px solid #ccc';
div1.style.margin = '5px 0';
// div1.style.backgroundColor = '#f9f9f9';

// Output the DOM element
output(div1);

// Scenario 2: Render directly to outEl container
if (outEl) {
  const div2 = document.createElement('div');
  div2.innerHTML = '<h3>Method 2: Direct to outEl</h3><p>Column C sum: ' + sumC + '</p>';
  div2.style.padding = '10px';
  div2.style.border = '1px solid #007acc';
  div2.style.margin = '5px 0';
  // div2.style.backgroundColor = '#e6f3ff';

  outEl.appendChild(div2);
} else {
  console.log('outEl container not available');
}
