// Import Danfo.js and create a small dataset
export var dfd = require('danfojs');
export var data = {
  A: [1, 2, 3],
  B: [10, 20, 30]
};
console.log('Danfo.js imported. Data:', data);

// Create DataFrame from data
export var df = new dfd.DataFrame(data);
console.log('DataFrame:');
df.print();

// Add a new column C = A + B
export var C = df['A'].add(df['B']);
export var df2 = df.addColumn('C', C, { inplace: false });
console.log('DataFrame with new column C:');
df2.print();

// Sum column C
export var sumC = df2['C'].sum();
console.log('Sum of column C:', sumC);
