// Import Danfo.js and create a small dataset
$.dfd = require('danfojs');
$.data = {
  A: [1, 2, 3],
  B: [10, 20, 30]
};
console.log('Danfo.js imported. Data:', $.data);

// Return the data object for display
return $.data;

// Create DataFrame from data
$.df = new $.dfd.DataFrame($.data);
console.log('DataFrame:');
$.df.print();

// Return the DataFrame for display
return $.df;

// Add a new column C = A + B
$.C = $.df['A'].add($.df['B']);
$.df2 = $.df.addColumn('C', $.C, { inplace: false });
console.log('DataFrame with new column C:');
$.df2.print();

// Return the new DataFrame
return $.df2;

// Sum column C
$.sumC = $.df2['C'].sum();
console.log('Sum of column C:', $.sumC);

// Return summary statistics
return {
  sumC: $.sumC,
  meanC: $.df2['C'].mean(),
  maxC: $.df2['C'].max(),
  minC: $.df2['C'].min()
};
