// Import Danfo.js and create a small dataset
exports.dfd = require('danfojs');
exports.data = {
  A: [1, 2, 3],
  B: [10, 20, 30]
};
console.log('Danfo.js imported. Data:', exports.data);

// Return module info
return {
  module: 'danfojs',
  version: exports.dfd.version || 'unknown',
  dataShape: { rows: exports.data.A.length, columns: Object.keys(exports.data).length }
};

// Create DataFrame from data
exports.df = new dfd.DataFrame(data);
console.log('DataFrame created:');
exports.df.print();

// Return DataFrame info
return {
  shape: exports.df.shape,
  columns: exports.df.columns,
  dtypes: exports.df.dtypes
};

// Add a new column C = A + B
exports.C = df['A'].add(df['B']);
exports.df2 = df.addColumn('C', exports.C, { inplace: false });
console.log('DataFrame with new column C:');
exports.df2.print();

// Return operation result
return {
  operation: 'addColumn',
  newColumn: 'C',
  newShape: exports.df2.shape,
  columnC_values: exports.C.values
};

// Sum column C
exports.sumC = df2['C'].sum();
console.log('Sum of column C:', exports.sumC);

// Return calculation summary
return {
  calculation: 'sum',
  column: 'C',
  result: exports.sumC,
  verification: exports.data.A.reduce((a, b) => a + b, 0) + exports.data.B.reduce((a, b) => a + b, 0)
};
