// Import Danfo.js library
const dfd = require('danfojs-node');

// Create sample sales data
const salesData = {
  month: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
  revenue: [45000, 52000, 48000, 61000, 55000, 67000, 73000, 69000, 58000, 62000, 71000, 78000],
  costs: [32000, 38000, 35000, 42000, 39000, 47000, 51000, 48000, 41000, 44000, 50000, 54000],
  customers: [450, 520, 480, 610, 550, 670, 730, 690, 580, 620, 710, 780]
};

// Export the DataFrame and data for other cells to use
exports.df = new dfd.DataFrame(salesData);
exports.salesData = salesData; // Export raw data object
console.log('Sales DataFrame created:');
console.log(exports.df);

// Calculate profit and profit margin (immutable operations)
exports.salesDataFrame = exports.df
  .addColumn('profit', exports.df['revenue'].sub(exports.df['costs']), { inplace: false })
  .addColumn('profit_margin', exports.df['revenue'].sub(exports.df['costs']).div(exports.df['revenue']).mul(100), { inplace: false });

console.log('\nDataFrame with calculated fields:');
console.log(exports.salesDataFrame);

// Method 1: Create Danfo.js bar chart using output()

const createChart1 = () => {
  const container = createContainer({
    style: 'border: 2px solid var(--color-success);'
  });

  const title = createTitle('üìä Method 1: Danfo.js Bar Chart via output()', 3, {
    style: 'color: var(--color-success); margin-bottom: 20px;'
  });
  container.appendChild(title);

  // Create plot container with unique ID
  const plotDiv = createDiv({
    style: 'width: 100%; height: 400px; margin: 10px 0;'
  });
  container.appendChild(plotDiv);

  // Use Danfo.js to create bar chart
  try {
    salesDataFrame.plot(plotDiv.id).bar({
      x: 'month',
      y: 'revenue',
      config: {
        displayModeBar: false,
        responsive: true
      },
      layout: {
        title: 'Monthly Revenue',
        xaxis: { title: 'Month' },
        yaxis: { title: 'Revenue ($)' },
        showlegend: false,
        plot_bgcolor: 'rgba(0,0,0,0)',
        paper_bgcolor: 'rgba(0,0,0,0)'
      }
    });

    console.log('‚úÖ Danfo.js bar chart created successfully using output()!');

  } catch (error) {
    console.error('Error creating Danfo.js bar chart:', error);
    plotDiv.innerHTML = `<div style="padding: 20px; text-align: center; color: var(--color-error);">Error: ${error.message}</div>`;
  }

  return container;
};

// Output the container with Danfo.js chart
output(createChart1());

console.log('Bar chart created using Danfo.js plot().bar() method');

