// Import Danfo.js library
const dfd = require('danfojs-node');

// Create sample sales data
const salesData = {
  month: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
  revenue: [45000, 52000, 48000, 61000, 55000, 67000, 73000, 69000, 58000, 62000, 71000, 78000],
  costs: [32000, 38000, 35000, 42000, 39000, 47000, 51000, 48000, 41000, 44000, 50000, 54000],
  customers: [450, 520, 480, 610, 550, 670, 730, 690, 580, 620, 710, 780]
};

// Export the DataFrame and data for other cells to use
exports.df = new dfd.DataFrame(salesData);
exports.salesData = salesData; // Export raw data object
console.log('Sales DataFrame created:');
console.log(exports.df);

// Calculate profit and profit margin (immutable operations)
exports.salesDataFrame = exports.df
  .addColumn('profit', exports.df['revenue'].sub(exports.df['costs']), { inplace: false })
  .addColumn('profit_margin', exports.df['revenue'].sub(exports.df['costs']).div(exports.df['revenue']).mul(100), { inplace: false });

console.log('\nDataFrame with calculated fields:');
console.log(exports.salesDataFrame);

// Demonstrate both DOM output approaches

// Method 1: Create DOM element and use output()
const createChart1 = () => {
  const container = document.createElement('div');
  container.style.cssText = 'padding: 20px; border: 2px solid #4CAF50; margin: 10px 0; background: #f9f9f9; border-radius: 8px;';

  const title = document.createElement('h3');
  title.textContent = 'Method 1: output(domElement)';
  title.style.cssText = 'margin: 0 0 15px 0; color: #2E7D32; font-family: Arial, sans-serif;';

  const chart = document.createElement('div');
  chart.style.cssText = 'display: flex; gap: 10px; flex-wrap: wrap;';

  // Create simple bar chart using divs
  const months = salesDataFrame['month'].values;
  const revenue = salesDataFrame['revenue'].values;
  const maxRevenue = Math.max(...revenue);

  months.forEach((month, i) => {
    const bar = document.createElement('div');
    const height = (revenue[i] / maxRevenue) * 100;
    bar.style.cssText = `
      width: 40px;
      height: ${height}px;
      background: linear-gradient(to top, #4CAF50, #8BC34A);
      margin: 0 2px;
      display: flex;
      flex-direction: column;
      justify-content: flex-end;
      align-items: center;
      border-radius: 4px 4px 0 0;
      position: relative;
    `;

    const label = document.createElement('div');
    label.textContent = month;
    label.style.cssText = 'font-size: 10px; margin-top: 5px; color: #333;';

    const value = document.createElement('div');
    value.textContent = `$${(revenue[i]/1000).toFixed(0)}k`;
    value.style.cssText = 'font-size: 8px; color: white; position: absolute; top: 2px;';

    bar.appendChild(value);
    chart.appendChild(bar);
    chart.appendChild(label);
  });

  container.appendChild(title);
  container.appendChild(chart);
  return container;
};

// Output the DOM element using output()
output(createChart1());

console.log('Chart 1 created using output() method');

