  title.style.cssText = 'margin: 20px 0 15px 0; color: #1976D2; font-family: Arial, sans-serif;';
  outEl.appendChild(title);

  // Create profit margin chart
  const chartContainer = document.createElement('div');
  chartContainer.style.cssText = 'padding: 20px; border: 2px solid #2196F3; background: #f0f8ff; border-radius: 8px; margin-bottom: 10px;';

  const chartTitle = document.createElement('h4');
  chartTitle.textContent = 'Monthly Profit Margin (%)';
  chartTitle.style.cssText = 'margin: 0 0 15px 0; color: #1565C0;';
  chartContainer.appendChild(chartTitle);

  // Create line chart for profit margin
  const lineChart = document.createElement('div');
  lineChart.style.cssText = 'position: relative; height: 120px; border-bottom: 2px solid #ccc; border-left: 2px solid #ccc; margin: 10px;';

  const months = salesDataFrame['month'].values;
  const profitMargins = salesDataFrame['profit_margin'].values;
  const maxMargin = Math.max(...profitMargins);
  const minMargin = Math.min(...profitMargins);
  const range = maxMargin - minMargin;

  // Create SVG for smooth line
  const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
  svg.style.cssText = 'position: absolute; top: 0; left: 0; width: 100%; height: 100%;';
  svg.setAttribute('viewBox', '0 0 360 120');

  let pathData = '';
  profitMargins.forEach((margin, i) => {
    const x = (i / (profitMargins.length - 1)) * 340 + 10;
    const y = 110 - ((margin - minMargin) / range) * 100;
    pathData += i === 0 ? `M ${x} ${y}` : ` L ${x} ${y}`;

    // Add data points
    const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
    circle.setAttribute('cx', x.toString());
    circle.setAttribute('cy', y.toString());
    circle.setAttribute('r', '3');
    circle.setAttribute('fill', '#FF5722');
    svg.appendChild(circle);

    // Add value labels
    const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    text.setAttribute('x', x.toString());
    text.setAttribute('y', (y - 8).toString());
    text.setAttribute('text-anchor', 'middle');
    text.setAttribute('font-size', '8');
    text.setAttribute('fill', '#333');
    text.textContent = margin.toFixed(1) + '%';
    svg.appendChild(text);
  });

  // Add the path
  const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
  path.setAttribute('d', pathData);
  path.setAttribute('stroke', '#FF5722');
  path.setAttribute('stroke-width', '2');
  path.setAttribute('fill', 'none');
  svg.appendChild(path);

  lineChart.appendChild(svg);
  chartContainer.appendChild(lineChart);

  // Add month labels
  const labelsContainer = document.createElement('div');
  labelsContainer.style.cssText = 'display: flex; justify-content: space-between; margin-top: 5px; padding: 0 10px;';
  months.forEach(month => {
    const label = document.createElement('span');
    label.textContent = month;
    label.style.cssText = 'font-size: 10px; color: #666;';
    labelsContainer.appendChild(label);
  });
  chartContainer.appendChild(labelsContainer);

  outEl.appendChild(chartContainer);

  console.log('Chart 2 created using direct outEl method');
} else {
  console.log('outEl container not available');
}

