// Method 1: Create table and output it
const createInteractiveTable = () => {
  const container = document.createElement('div');
  container.style.cssText = 'margin: 20px 0; padding: 15px; border: 1px solid #ddd; border-radius: 8px; background: white;';

  const title = document.createElement('h3');
  title.textContent = 'Interactive Sales Data Table';
  title.style.cssText = 'margin: 0 0 15px 0; color: #333;';
  container.appendChild(title);

  const table = document.createElement('table');
  table.style.cssText = 'width: 100%; border-collapse: collapse; font-family: Arial, sans-serif;';

  // Create header
  const header = document.createElement('tr');
  ['Month', 'Revenue', 'Costs', 'Profit', 'Margin %'].forEach(text => {
    const th = document.createElement('th');
    th.textContent = text;
    th.style.cssText = 'padding: 10px; border: 1px solid #ddd; background: #f5f5f5; text-align: left;';
    header.appendChild(th);
  });
  table.appendChild(header);

  // Get data from exported DataFrame - use dependency on salesDataFrame
  const months = salesDataFrame['month'].values;
  const revenue = salesDataFrame['revenue'].values;
  const costs = salesDataFrame['costs'].values;
  const profit = salesDataFrame['profit'].values;
  const margins = salesDataFrame['profit_margin'].values;

  months.forEach((month, i) => {
    const row = document.createElement('tr');
    row.style.cssText = 'cursor: pointer; transition: background-color 0.2s;';
    row.onmouseover = () => row.style.backgroundColor = '#f0f0f0';
    row.onmouseout = () => row.style.backgroundColor = 'white';

    [month, `$${revenue[i].toLocaleString()}`, `$${costs[i].toLocaleString()}`, 
     `$${profit[i].toLocaleString()}`, `${margins[i].toFixed(1)}%`].forEach(text => {
      const td = document.createElement('td');
      td.textContent = text;
      td.style.cssText = 'padding: 8px 10px; border: 1px solid #ddd;';
      row.appendChild(td);
    });

    table.appendChild(row);
  });

  container.appendChild(table);
  return container;
};

