// Import required Node.js modules
const fs = require('fs');
const path = require('path');
const os = require('os');

// Get current working directory and system info
$.currentDir = process.cwd();
$.homeDir = os.homedir();
$.platform = os.platform();

console.log('ðŸ“ Current directory:', $.currentDir);
console.log('ðŸ  Home directory:', $.homeDir);
console.log('ðŸ’» Platform:', $.platform);

// List files in current directory
const files = fs.readdirSync($.currentDir);
$.fileList = files;

console.log('ðŸ“‹ Files in current directory:');
files.forEach((file, index) => {
  const fullPath = path.join($.currentDir, file);
  const stats = fs.statSync(fullPath);
  const type = stats.isDirectory() ? 'ðŸ“ DIR' : 'ðŸ“„ FILE';
  const size = stats.isFile() ? `${stats.size} bytes` : '';
  console.log(`${index + 1}. ${type} ${file} ${size}`);
});

// Try to read package.json if it exists
const packageJsonPath = path.join($.currentDir, 'package.json');
let packageData = null;

if (fs.existsSync(packageJsonPath)) {
  try {
    const packageContent = fs.readFileSync(packageJsonPath, 'utf8');
    packageData = JSON.parse(packageContent);
    $.projectName = packageData.name || 'Unknown';
    $.projectVersion = packageData.version || '0.0.0';
    $.dependencies = Object.keys(packageData.dependencies || {});

    console.log('ðŸ“¦ Package Information:');
    console.log('  Name:', $.projectName);
    console.log('  Version:', $.projectVersion);
    console.log('  Dependencies:', $.dependencies.length);
    console.log('ðŸ“‹ Full package.json:');
    console.log(packageData);
  } catch (error) {
    console.error('âŒ Error reading package.json:', error.message);
    $.projectName = 'Error reading package';
    $.projectVersion = '0.0.0';
    $.dependencies = [];
  }
} else {
  console.log('âš ï¸ No package.json found in current directory');
  $.projectName = 'No package.json found';
  $.projectVersion = '0.0.0';
  $.dependencies = [];
}

// File analysis using reactive calculations
$.totalFiles = $.fileList.filter(file => {
  const fullPath = path.join($.currentDir, file);
  return fs.statSync(fullPath).isFile();
}).length;

$.totalDirectories = $.fileList.filter(file => {
  const fullPath = path.join($.currentDir, file);
  return fs.statSync(fullPath).isDirectory();
}).length;

console.log(`ðŸ“Š Analysis: ${$.totalFiles} files, ${$.totalDirectories} directories`);
console.log(`Dependencies count: ${$.dependencies.length}`);

