// Advanced file operations with crypto hashing
const crypto = require('crypto');

function getFileHash(filePath) {
  try {
    if (!fs.existsSync(filePath)) return null;
    const content = fs.readFileSync(filePath);
    return crypto.createHash('md5').update(content).digest('hex');
  } catch (error) {
    return `Error: ${error.message}`;
  }
}

// Analyze common project files
const commonFiles = ['package.json', 'README.md', 'index.js', 'src/index.ts', 'tsconfig.json'];
const fileHashes = {};
let analyzedCount = 0;

console.log('ðŸ” Analyzing common project files:');
commonFiles.forEach(fileName => {
  const filePath = path.join($.currentDir, fileName);
  const hash = getFileHash(filePath);

  if (hash && !hash.startsWith('Error:')) {
    fileHashes[fileName] = hash;
    const stats = fs.statSync(filePath);
    console.log(`ðŸ”’ ${fileName}: ${hash.substring(0, 8)}... (${stats.size} bytes)`);
    analyzedCount++;
  } else if (fs.existsSync(filePath)) {
    console.log(`âŒ Error hashing ${fileName}: ${hash}`);
  }
});

$.analyzedFiles = fileHashes;
$.fileCount = analyzedCount;

console.log(`\nâœ… Successfully analyzed ${$.fileCount} files with MD5 hashes`);

// Create a comprehensive system report
const systemReport = {
  system: {
    platform: $.platform,
    currentDirectory: $.currentDir,
    homeDirectory: $.homeDir
  },
  project: {
    name: $.projectName,
    version: $.projectVersion,
    dependencyCount: $.dependencies.length
  },
  filesystem: {
    totalFiles: $.totalFiles,
    totalDirectories: $.totalDirectories,
    analyzedFiles: $.fileCount,
    fileHashes: Object.keys($.analyzedFiles)
  },
  timestamp: new Date().toISOString()
};

$.report = systemReport;

console.log('ðŸ“Š System Report Generated:');
console.log(systemReport);

// Calculate some derived metrics
$.filesPerDirectory = $.totalDirectories > 0 ? ($.totalFiles / $.totalDirectories).toFixed(2) : $.totalFiles;
$.hashCoverage = $.totalFiles > 0 ? (($.fileCount / $.totalFiles) * 100).toFixed(1) : '0';

console.log(`\nðŸ“ˆ Derived Metrics:`);
console.log(`  Files per directory: ${$.filesPerDirectory}`);
console.log(`  Hash coverage: ${$.hashCoverage}%`);

