console.log('Starting tax calculation...');

exports.taxRate = 0.08;
const initialTaxAmount = total * exports.taxRate;

console.log('Initial calculation:', { total, taxRate: exports.taxRate, initialTaxAmount });

if (total > 500) {
    exports.taxRate = 0.10;
    console.log('Luxury tax rate applied: 10%');
} else if (total > 200) {
    exports.taxRate = 0.085;
    console.log('Medium tax rate applied: 8.5%');
} else {
    console.log('Standard tax rate maintained: 8%');
}

exports.finalTaxAmount = total * exports.taxRate;
exports.finalTotal = total + exports.finalTaxAmount;

exports.calculationSummary = {
    pricing: { basePrice: price, quantity: quantity, subtotal: total },
    tax: { rate: exports.taxRate, amount: exports.finalTaxAmount, bracket: total > 500 ? 'luxury' : total > 200 ? 'medium' : 'standard' },
    totals: { beforeTax: total, afterTax: exports.finalTotal, savings: subtotal - total },
    metadata: { calculatedAt: new Date().toISOString(), breakdown: [subtotal, -discount, exports.finalTaxAmount] }
};

console.log('Final calculation:', exports.calculationSummary);
console.info('Tax calculation completed successfully!');

// Output summary for display
output(
    // Tax bracket info
    {
        taxBracket: exports.calculationSummary.tax.bracket,
        effectiveRate: exports.taxRate,
        totalSavings: exports.calculationSummary.totals.savings,
        finalAmount: exports.finalTotal
    },
    // Detailed breakdown
    exports.calculationSummary
);
