Object.entries(breakdown).forEach(([label, amount]) => {
  const sign = amount >= 0 ? '+' : '';
  console.log(`${label}: ${sign}$${amount.toFixed(2)}`);
});

// Export the breakdown for potential use elsewhere
exports.priceBreakdown = breakdown;

