// Calculate Shopping List Totals using DataFrame operations
console.log('=== Shopping List Calculations ===');

if (shoppingList) {
  console.log('ï¿½ Calculating totals using DataFrame operations...');

  // Create a calculation DataFrame with subtotal column
  const calculationData = {
    item: shoppingList.loc({columns: ['item']}).values.flat(),
    price: shoppingList.loc({columns: ['price']}).values.flat(),
    quantity: shoppingList.loc({columns: ['quantity']}).values.flat()
  };

  // Add calculated subtotal column
  calculationData.subtotal = calculationData.price.map((price, index) => 
    price * calculationData.quantity[index]
  );

  const calculationDF = new dfd.DataFrame(calculationData);


  console.log('\n Calculation DataFrame (with subtotals):');
  calculationDF.print();

  // Sum the columns to get totals
  const totalCost = calculationDF.subtotal.sum();
  const totalItems = calculationDF.quantity.sum();
  const itemCount = shoppingList.shape[0];

  console.log('\nğŸ“‹ Summary:');
  console.log('â”'.repeat(30));
  console.log(`Different Products: ${itemCount}`);
  console.log(`Total Items: ${totalItems}`);
  console.log(`Total Cost: $${totalCost.toFixed(2)}`);
  console.log(`Average per Item: $${(totalCost / totalItems).toFixed(2)}`);

  // Export for reactive display
  exports.totalCost = totalCost;
  exports.totalItems = totalItems;
  exports.itemCount = itemCount;
  exports.averagePerItem = totalItems > 0 ? totalCost / totalItems : 0;
  exports.calculationTimestamp = new Date().toISOString();

  console.log('\nâœ… Calculations complete and exported!');

} else {
  console.log('âŒ No shopping list available. Run the initialization cell first.');
}

// Storage Management - Save/Load Shopping List
console.log('=== Storage Management ===');

// Save current shopping list to storage
if (shoppingList) {
  console.log('ğŸ’¾ Saving shopping list to storage...');

  // Convert DataFrame to JSON for storage
  const listData = {
    item: shoppingList.loc({columns: ['item']}).values.flat(),
    price: shoppingList.loc({columns: ['price']}).values.flat(),
    quantity: shoppingList.loc({columns: ['quantity']}).values.flat(),
    savedAt: new Date().toISOString()
  };

  storage.set('shopping_list', listData);
  storage.set('shopping_list_updated', new Date().toISOString());
  console.log('âœ… Shopping list saved to storage!');
  console.log(`ï¿½ Saved ${listData.item.length} items at ${new Date(listData.savedAt).toLocaleString()}`);

} else {
  console.log('âš ï¸ No shopping list to save');
}

// Show storage status
console.log('\nğŸ“¦ Current Storage Contents:');
console.log('â”'.repeat(30));
const keys = storage.keys();
if (keys.length > 0) {
  keys.forEach(key => {
    const value = storage.get(key);
    if (key === 'shopping_list' && value) {
      console.log(`${key}: ${value.item?.length || 0} items (saved: ${new Date(value.savedAt).toLocaleString()})`);
    } else {
      console.log(`${key}: ${typeof value} (${JSON.stringify(value).slice(0, 50)}...)`);
    }
  });
} else {
  console.log('(empty)');
}

console.log('\nï¿½ To load from storage, run: `const saved = storage.get(\'shopping_list\');`');
