cells.code:
// OS module is available as an injected global variable
exports.platform = os.platform();
exports.homedir = os.homedir();
console.log('Platform:', exports.platform);
console.log('Home directory:', exports.homedir);

// Output system summary object
output({
  system: 'Node.js',
  platform: exports.platform,
  homeDirectory: exports.homedir,
  processId: process.pid,
  nodeVersion: process.version
});

// Export a message based on membership using exports object
exports.memberMessage = isMember ? `Welcome, ${username}!` : `Please sign up, ${username}.`;
console.log(exports.memberMessage);

// Output multiple values
output(
  // User info
  { name: username, age: age, isMember: isMember },
  // Message
  exports.memberMessage,
  // Recommendation
  {
    recommendation: age < 18 ? 'Youth discount available' : age > 65 ? 'Senior discount available' : 'Standard pricing',
    timestamp: new Date().toISOString()
  }
);

