cells.code:
// Import os module and export platform info
exports.os = require('os');
exports.platform = os.platform();
exports.homedir = os.homedir();
exports.cpuCount = os.cpus().length;

console.log('Platform:', exports.platform);
console.log('Home directory:', exports.homedir);
console.log('CPU cores:', exports.cpuCount);

// Output system summary
output({
  system: {
    platform: exports.platform,
    homeDirectory: exports.homedir,
    cpuCores: exports.cpuCount,
    totalMemory: Math.round(os.totalmem() / 1024 / 1024 / 1024) + ' GB',
    nodeVersion: process.version
  }
});

// Process membership status and generate personalized content
exports.memberMessage = isMember ? `Welcome back, ${username}!` : `Join us today, ${username}!`;
exports.ageGroup = age < 18 ? 'Youth' : age < 65 ? 'Adult' : 'Senior';
exports.discount = age < 18 ? 0.15 : age >= 65 ? 0.10 : (isMember ? 0.05 : 0);

console.log('Member message:', exports.memberMessage);
console.log('Age group:', exports.ageGroup);
console.log('Discount rate:', exports.discount);

// Output user profile and recommendations
output(
  {
    userProfile: {
      name: username,
      age: age,
      ageGroup: exports.ageGroup,
      isMember: isMember,
      discountRate: exports.discount
    }
  },
  {
    message: exports.memberMessage,
    recommendation: exports.discount > 0 ? `You qualify for a ${Math.round(exports.discount * 100)}% discount!` : 'Consider membership for exclusive benefits',
    timestamp: new Date().toISOString()
  }
);

