// Import TensorFlow.js
exports.tf = require('@tensorflow/tfjs');
console.log('TensorFlow.js imported successfully');
console.log('Backend:', exports.tf.getBackend());

// Output TensorFlow info
output({
  library: 'TensorFlow.js',
  version: exports.tf.version_core || 'unknown',
  backend: exports.tf.getBackend(),
  memory: exports.tf.memory()
});

// Create sample data based on user preferences
if (useRandomData) {
  // Create random data
  exports.data1 = tf.randomUniform([arraySize], 0, 100).arraySync();
  exports.data2 = tf.randomUniform([arraySize], 0, 50).arraySync();
} else {
  // Create sequential data
  exports.data1 = Array.from({length: arraySize}, (_, i) => i + 1);
  exports.data2 = Array.from({length: arraySize}, (_, i) => (i + 1) * 0.5);
}

console.log('Data 1:', exports.data1);
console.log('Data 2:', exports.data2);

// Output data info
output({
  dataType: useRandomData ? 'random' : 'sequential',
  arraySize: arraySize,
  data1_sample: exports.data1.slice(0, 5),
  data2_sample: exports.data2.slice(0, 5)
});

// Convert arrays to tensors and export them
exports.tensor1 = tf.tensor1d(data1);
exports.tensor2 = tf.tensor1d(data2);

console.log('Tensor 1 shape:', exports.tensor1.shape);
console.log('Tensor 2 shape:', exports.tensor2.shape);

// Basic tensor info
exports.tensor1Info = {
  shape: exports.tensor1.shape,
  dtype: exports.tensor1.dtype,
  size: exports.tensor1.size
};

console.log('Tensor 1 info:', exports.tensor1Info);

// Output tensor information
output({
  tensor1: {
    shape: exports.tensor1.shape,
    dtype: exports.tensor1.dtype,
    size: exports.tensor1.size
  },
  tensor2: {
    shape: exports.tensor2.shape,
    dtype: exports.tensor2.dtype,
    size: exports.tensor2.size
  }
});

// Basic mathematical operations
tf.setBackend('webgl')

exports.sum1 = tf.sum(tensor1).arraySync();
exports.mean1 = tf.mean(tensor1).arraySync();
exports.std1 = tf.moments(tensor1).variance.sqrt().arraySync();
exports.min1 = tf.min(tensor1).arraySync();
exports.max1 = tf.max(tensor1).arraySync();

console.log('Statistics for Tensor 1:');
console.log('Sum:', exports.sum1);
console.log('Mean:', exports.mean1);
console.log('Standard Deviation:', exports.std1);
console.log('Min:', exports.min1);
console.log('Max:', exports.max1);

// Output statistics
output({
  operation: 'Basic Statistics',
  tensor: 'data1',
  statistics: {
    sum: exports.sum1,
    mean: exports.mean1,
    std: exports.std1,
    min: exports.min1,
    max: exports.max1,
    range: exports.max1 - exports.min1
  }
});

// Element-wise operations
exports.added = tf.add(tensor1, tensor2).arraySync();
exports.multiplied = tf.mul(tensor1, tensor2).arraySync();
exports.scaled = tf.mul(tensor1, multiplier).arraySync();
exports.squared = tf.square(tensor1).arraySync();
exports.sqrt_vals = tf.sqrt(tf.abs(tensor1)).arraySync();

console.log('Element-wise operations:');
console.log('Added (first 5):', exports.added.slice(0, 5));
console.log('Multiplied (first 5):', exports.multiplied.slice(0, 5));
console.log('Scaled by', multiplier, '(first 5):', exports.scaled.slice(0, 5));

// Output operation results
output({
  operation: 'Element-wise Operations',
  results: {
    addition_sample: exports.added.slice(0, 3),
    multiplication_sample: exports.multiplied.slice(0, 3),
    scaling_factor: multiplier,
    scaled_sample: exports.scaled.slice(0, 3),
    squared_sample: exports.squared.slice(0, 3)
  }
});

// Matrix operations - reshape to 2D for demonstration
const matrixSize = Math.floor(Math.sqrt(arraySize));
const actualSize = matrixSize * matrixSize;

// Create matrices and export them
exports.matrix1 = tf.tensor2d(data1.slice(0, actualSize), [matrixSize, matrixSize]);
exports.matrix2 = tf.tensor2d(data2.slice(0, actualSize), [matrixSize, matrixSize]);

// Matrix operations
exports.matrixSum = tf.add(exports.matrix1, exports.matrix2).arraySync();
exports.matrixProduct = tf.matMul(exports.matrix1, exports.matrix2).arraySync();
exports.transpose1 = tf.transpose(exports.matrix1).arraySync();

// Calculate trace (sum of diagonal elements) instead of determinant
const diagonalElements = [];
for (let i = 0; i < matrixSize; i++) {
  diagonalElements.push(exports.matrix1.arraySync()[i][i]);
}
exports.trace = diagonalElements.reduce((sum, val) => sum + val, 0);

console.log(`Matrix operations (${matrixSize}x${matrixSize}):`);
console.log('Matrix 1:', exports.matrix1.arraySync());
console.log('Matrix sum:', exports.matrixSum);
console.log('Trace (sum of diagonal):', exports.trace);

// Output matrix operations
output({
  operation: 'Matrix Operations',
  matrixSize: `${matrixSize}x${matrixSize}`,
  trace: exports.trace,
  matrixSum_diagonal: exports.matrixSum.map((row, i) => row[i]),
  transpose_shape: [matrixSize, matrixSize]
});

// Advanced statistical operations
exports.normalized1 = tf.div(tf.sub(tensor1, tf.mean(tensor1)), tf.moments(tensor1).variance.sqrt()).arraySync();
exports.cumsum1 = tf.cumsum(tensor1).arraySync();
exports.sorted1 = tf.topk(tensor1, arraySize).values.arraySync();

// Percentiles (approximate using sorting)
const sortedTensor = tf.topk(tensor1, arraySize).values;
exports.median = tf.slice(sortedTensor, [Math.floor(arraySize/2)], [1]).arraySync()[0];
exports.q25 = tf.slice(sortedTensor, [Math.floor(arraySize*0.25)], [1]).arraySync()[0];
exports.q75 = tf.slice(sortedTensor, [Math.floor(arraySize*0.75)], [1]).arraySync()[0];

console.log('Advanced operations:');
console.log('Normalized (first 5):', exports.normalized1.slice(0, 5));
console.log('Cumulative sum (first 5):', exports.cumsum1.slice(0, 5));
console.log('Median:', exports.median);
console.log('Q25:', exports.q25, 'Q75:', exports.q75);

// Clean up intermediate tensors
sortedTensor.dispose();

// Output advanced statistics
output({
  operation: 'Advanced Statistics',
  quartiles: {
    q25: exports.q25,
    median: exports.median,
    q75: exports.q75,
    iqr: exports.q75 - exports.q25
  },
  normalized_stats: {
    mean: tf.mean(tf.tensor1d(exports.normalized1)).arraySync(),
    std: tf.moments(tf.tensor1d(exports.normalized1)).variance.sqrt().arraySync()
  },
  cumsum_final: exports.cumsum1[exports.cumsum1.length - 1]
});

// Clean up tensors to free memory
exports.initialMemory = tf.memory().numTensors;

// Safely dispose of tensors with existence checks
const tensorsToDispose = [tensor1, tensor2, matrix1, matrix2];
const tensorNames = ['tensor1', 'tensor2', 'matrix1', 'matrix2'];
let disposedCount = 0;

tensorsToDispose.forEach((tensor, index) => {
  if (tensor && typeof tensor.dispose === 'function') {
    try {
      tensor.dispose();
      disposedCount++;
      console.log(`Disposed ${tensorNames[index]}`);
    } catch (error) {
      console.warn(`Failed to dispose ${tensorNames[index]}:`, error.message);
    }
  } else {
    console.warn(`${tensorNames[index]} is not available for disposal`);
  }
});

exports.finalMemory = tf.memory().numTensors;
exports.tensorsFreed = exports.initialMemory - exports.finalMemory;

console.log('Memory cleanup:');
console.log('Initial tensors:', exports.initialMemory);
console.log('Final tensors:', exports.finalMemory);
console.log('Tensors disposed:', disposedCount);
console.log('Net tensors freed:', exports.tensorsFreed);

// Output memory info
output({
  memoryManagement: {
    initialTensors: exports.initialMemory,
    finalTensors: exports.finalMemory,
    tensorsDisposed: disposedCount,
    tensorsFreed: exports.tensorsFreed,
    memoryInfo: tf.memory()
  }
});

