// Generate sample sales data
const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'];
const products = ['Widget A', 'Widget B', 'Widget C'];

const salesData = [];
for (let month of months) {
  for (let product of products) {
    salesData.push({
      month,
      product,
      sales: Math.floor(Math.random() * 1000) + 500,
      cost: Math.floor(Math.random() * 300) + 200
    });
  }
}

exports.salesData = salesData;
console.log(`Generated ${salesData.length} sales records`);
console.log('Sample record:', salesData[0]);

// Calculate key metrics (automatically updates when salesData changes)
const totalSales = salesData.reduce((sum, item) => sum + item.sales, 0);
const totalCost = salesData.reduce((sum, item) => sum + item.cost, 0);
const profit = totalSales - totalCost;
const profitMargin = (profit / totalSales) * 100;

// Group by product
const productTotals = salesData.reduce((acc, item) => {
  if (!acc[item.product]) {
    acc[item.product] = { sales: 0, cost: 0, profit: 0 };
  }
  acc[item.product].sales += item.sales;
  acc[item.product].cost += item.cost;
  acc[item.product].profit += (item.sales - item.cost);
  return acc;
}, {});

exports.totalSales = totalSales;
exports.totalCost = totalCost;
exports.profit = profit;
exports.profitMargin = profitMargin;
exports.productTotals = productTotals;

console.log('ğŸ“Š Business Metrics:');
console.log(`Total Sales: $${totalSales.toLocaleString()}`);
console.log(`Total Cost: $${totalCost.toLocaleString()}`);
console.log(`Profit: $${profit.toLocaleString()}`);
console.log(`Profit Margin: ${profitMargin.toFixed(1)}%`);

// Display data in an interactive table
console.log('ğŸ“‹ Raw Sales Data:');
output.table(salesData);

console.log('\nğŸ† Product Performance:');
output.table(Object.entries(productTotals).map(([product, data]) => ({
  Product: product,
  Sales: `$${data.sales.toLocaleString()}`,
  Cost: `$${data.cost.toLocaleString()}`,
  Profit: `$${data.profit.toLocaleString()}`,
  'Profit Margin': `${((data.profit / data.sales) * 100).toFixed(1)}%`
})));

// Create a bar chart showing product performance
const chartData = Object.entries(productTotals).map(([product, data]) => ({
  x: product,
  y: data.profit,
  type: 'bar',
  marker: {
    color: ['#2E86AB', '#A23B72', '#F18F01'][Object.keys(productTotals).indexOf(product)]
  }
}));

const layout = {
  title: 'Product Profit Comparison',
  xaxis: { title: 'Product' },
  yaxis: { title: 'Profit ($)' },
  plot_bgcolor: 'rgba(0,0,0,0)',
  paper_bgcolor: 'rgba(0,0,0,0)'
};

const config = {
  responsive: true,
  displayModeBar: true
};

// Create container for the plot
const plotDiv = createDiv({ 
  id: 'profit-chart', 
  style: 'width: 100%; height: 400px;' 
});
output(plotDiv);

// Note: Plotly would be loaded here if available
// Plotly.newPlot('profit-chart', chartData, layout, config);

console.log('ğŸ“Š Chart data prepared');
console.log('Chart shows profit by product:', chartData.map(d => `${d.x}: $${d.y.toLocaleString()}`));

// Pre-loaded scientific libraries
console.log('ğŸ“¦ Built-in Scientific Libraries:');
console.log('- math (Math.js): Advanced mathematical functions');
console.log('- lodash (_): Utility functions for data manipulation');
console.log('- moment: Date/time manipulation (if available)');
console.log('- axios: HTTP client for API requests (if available)');

// Math.js example - Advanced mathematics
const mathExample = {
  // Basic statistics
  mean: salesData.reduce((sum, item) => sum + item.sales, 0) / salesData.length,
  max: Math.max(...salesData.map(item => item.sales)),
  min: Math.min(...salesData.map(item => item.sales)),

  // Advanced math with Math.js (if available)
  standardDeviation: (() => {
    const values = salesData.map(item => item.sales);
    try {
      // Try to use math.js for better precision
      return typeof math !== 'undefined' ? math.std(values) : 'N/A';
    } catch {
      // Fallback to manual calculation
      const mean = values.reduce((a, b) => a + b) / values.length;
      const variance = values.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / values.length;
      return Math.sqrt(variance);
    }
  })()
};

exports.mathExample = mathExample;
console.log('\nğŸ”¢ Enhanced Statistics:');
console.log(`Mean: $${mathExample.mean.toFixed(0)}`);
console.log(`Standard Deviation: ${typeof mathExample.standardDeviation === 'number' ? mathExample.standardDeviation.toFixed(0) : mathExample.standardDeviation}`);
console.log(`Range: $${mathExample.min} - $${mathExample.max}`);

