// Demonstrate data persistence and storage
const storageExample = {
  timestamp: new Date().toISOString(),
  sessionData: {
    userName,
    userAge,
    colorTheme,
    notifications,
    calculatedValues: {
      baseValue,
      doubled,
      percentage
    }
  },
  businessMetrics: {
    totalSales,
    profit,
    profitMargin
  }
};

exports.storageExample = storageExample;

console.log('ğŸ’¾ Session Data Snapshot:');
console.log('Captured at:', storageExample.timestamp);
console.log('User:', storageExample.sessionData.userName);
console.log('Metrics captured successfully!');

// This data could be saved to files, databases, or shared with other notebooks
output(storageExample);

// Generate a summary of this tutorial session
const summary = {
  tutorial: 'Welcome to Nodebook.js',
  featuresExplored: [
    'Reactive Programming',
    'Interactive Inputs',
    'Data Processing',
    'Visualization',
    'Module System',
