  ],
  userProfile: {
    name: userName,
    age: userAge,
    preferences: {
      theme: colorTheme,
      notifications
    }
  },
  sampleAnalysis: {
    recordsProcessed: salesData.length,
    totalRevenue: totalSales,
    profitMargin: Math.round(profitMargin * 100) / 100
  },
  completedAt: new Date().toLocaleString()
};

exports.tutorialSummary = summary;

console.log('ğŸ“ Tutorial Complete!');
console.log('Summary:', summary);
output(summary);
